mapper.select:
select

from video
where category_id = #{categoryId, jdbcType=INTEGER}

  and status = #{status, jdbcType=TINYINT}


  and publish_time >= #{startTime,jdbcType=TIMESTAMP}


  and publish_time <= #{endTime,jdbcType=TIMESTAMP}


  and (title like #{keyword} or author like #{keyword} or keywords like #{keyword})


  order by ${order}

select

from video left join video_data on id = video_id
where id = #{id,jdbcType=INTEGER}
